import {cloneSym} from "./Cloneable.js";

/**
 * A class to clone primitive values (type undefined, boolean, number, string,
 * symbol of function) and acyclic or cyclic arrays and objects. There is some
 * special support for Date and RegExp objects.
 */
export default class Cloner {

    /**
     *
     */
    constructor() {

        /**
         * How deep calls to the clone method are nested.
         *
         * @type {Number}
         * @private
         */
        this.recursionLevel = 0;

        /**
         * A mapping from original objects to their clones.
         *
         * @type {Map<Object, Object>}
         * @private
         */
        this.context = new Map();
    }

    /**
     * Tries to clone the given parameter.
     *
     * @param {*} p
     * The value to clone.
     *
     * @return {*}
     * A clone of the value.
     *
     * @throws {Error}
     * If the cloning fails because a type was not supported.
     */
    clone(p) {
        let result;

        this.recursionLevel++;
        if (this.isPrimitive(p)) {
            result = p;
        } else if (this.context.has(p)) {
            result = this.context.get(p);
        } else if (this.isDate(p)) {
            result = this.cloneDate(p);
        } else if (this.isRegExp(p)) {
            result = this.cloneRegExp(p);
        } else if (this.isArray(p)) {
            result = this.cloneArray(p);
        } else if (this.isObject(p)) {
            result = this.cloneObject(p);
        } else {
            throw new Error(`${p} cannot be cloned.`);
        }
        this.recursionLevel--;
        this.cleanup();

        return result;
    }

    /**
     * Tests if the given parameter is just a primitive value, i. e. it is not
     * an object or null.
     *
     * @param {*} p
     * The parameter to test.
     *
     * @return {Boolean}
     * If the given parameter is primitive.
     *
     * @private
     */
    isPrimitive(p) {
        return p === null || typeof p !== "object";
    }

    /**
     * Tests if the given parameter is a Date object.
     *
     * @param {*} p
     * The parameter to test.
     *
     * @return {Boolean}
     * If the given parameter is a Date object.
     *
     * @private
     */
    isDate(p) {
        return p instanceof Date;
    }

    /**
     * Clones a Date object
     *
     * @param {Date} date
     * The Date object to clone.
     *
     * @return {Date}
     * A clone of the Date object.
     *
     * @private
     */
    cloneDate(date) {
        let result = new Date(date.getTime());
        this.context.set(date, result);
        return result;
    }

    /**
     * Tests if the given parameter is a RegExp object.
     *
     * @param {*} p
     * The parameter to test.
     *
     * @return {Boolean}
     * If the given parameter is a RegExp object.
     *
     * @private
     */
    isRegExp(p) {
        return p instanceof RegExp;
    }

    /**
     * Clones a RegExp object.
     *
     * @param {RegExp} regExp
     * The RegExp object to clone.
     *
     * @return {RegExp}
     * A clone of the RegExp object.
     *
     * @private
     */
    cloneRegExp(regExp) {
        let result = new RegExp(regExp);
        this.context.set(regExp, result);
        return result;
    }

    /**
     * Tests if the given parameter is an array.
     *
     * @param {*} p
     * The parameter to test.
     *
     * @return {Boolean}
     * If the given parameter is an array.
     *
     * @private
     */
    isArray(p) {
        return Array.isArray(p);
    }

    /**
     * Clones an array.
     *
     * @param {Array} arr
     * The array to clone.
     *
     * @return {Array}
     * A clone of the array.
     *
     * @private
     */
    cloneArray(arr) {
        let result = [];
        this.context.set(arr, result);
        for (let v of arr) {
            result.push(this.cloneChild(v));
        }
        return result;
    }

    /**
     * Tests if the given parameter is an object.
     *
     * @param {*} p
     * The parameter to test.
     *
     * @return {Boolean}
     * If the given parameter is an object.
     *
     * @private
     */
    isObject(p) {
        return p instanceof Object;
    }

    /**
     * Clones an object.
     *
     * @param {Object} obj
     * The object to clone.
     *
     * @return {Object}
     * A clone of the object.
     *
     * @private
     */
    cloneObject(obj) {
        let result = {};
        this.context.set(obj, result);
        for (var [k, v] of Object.entries(obj)) {
            result[k] = this.cloneChild(v);
        }
        return result;
    }

    /**
     * Clones a value in a container.
     *
     * @param {*} child
     * The value to clone.
     *
     * @return {*}
     * A clone of the value.
     *
     * @private
     */
    cloneChild(child) {
        return this.isCloneable(child) ? this.cloneCloneable(child) : this.clone(child);
    }

    /**
     * Tests if the given parameter implements the Cloneable interface.
     *
     * @param {*} p
     * The parameter to test.
     *
     * @return {Boolean}
     * If the given parameter is Cloneable.
     *
     * @private
     */
    isCloneable(p) {
        return typeof p === "object" && typeof p[cloneSym] === "function";
    }

    /**
     * Clones a Cloneable object.
     *
     * @param {Object} cloneable
     * The object to clone.
     *
     * @return {Object}
     * A clone of the object.
     *
     * @private
     */
    cloneCloneable(cloneable) {
        return cloneable[cloneSym](this);
    }

    /**
     * Clears the context once the cloning is completed.
     *
     * @private
     */
    cleanup() {
        if (this.recursionLevel === 0) {
            this.context.clear();
        }
    }
}

/**
 * An event that can be fired and then catched by listeners.
 */
export default class Event {

    /**
     * @param {Object} source
     * The object firing this event.
     *
     * @param {String} type
     * The type of this event.
     *
     * @param {*} [data]
     * Any additional data to associate with this event.
     */
    constructor(source, type, data) {

        /**
         * The object firing this event.
         *
         * @type {Object}
         */
        this.source = source;

        /**
         * The type of this event.
         *
         * @type {String}
         */
        this.type = type;

        /**
         * Any additional data to associate with this event.
         *
         * @type {*}
         */
        this.data = data;
    }
}

import Event from "./Event.js";

/**
 * A helper class that can manage events and event listeners for other objects.
 * If you want to hide that you are using this class, you can use the provided
 * {@link observableMixin}.
 *
 * @implements {Observable}
 */
export default class EventManager {

    /**
     * A factory method for {@link Event} objects.
     *
     * @param {Object} source
     * The object firing this event.
     *
     * @param {String} type
     * The type of this event.
     *
     * @param {*} data
     * Any additional data to associate with this event.
     *
     * @return {Event}
     * The new Event object.
     */
    static makeEvent(source, type, data) {
        return new Event(source, type, data);
    }

    /**
     *
     */
    constructor() {

        /**
         * The registered listeners ordered by event type.
         *
         * @type {Map<String, Function[]>}
         */
        this.listeners = new Map();
    }

    /**
     * Adds an event listener to this event manager.
     *
     * @param {String|Iterator<String>} types
     * The types of events the function should listen to.
     *
     * @param {Function} f
     * The function that should be executed when the event fires.
     *
     * @param {Object} [context]
     * The object this should refer to inside f.
     *
     * @return {EventManager}
     * This event manager to make the method chainable.
     */
    addListener(types, f, context) {
        if (typeof types === "string") {
            types = [types];
        }
        if (context) {
            f = f.bind(context);
        }

        for (let type of types) {
            if (!this.listeners.has(type)) {
                this.listeners.set(type, []);
            }
            this.listeners.get(type).push(f);
        }

        return this;
    }

    /**
     * Notifies all listeners that are interested in the given event.
     *
     * @param {Event} e
     * The event to fire.
     *
     * @return {EventManager}
     * This event manager to make the method chainable.
     */
    fireEvent(e) {
        const listeners = this.listeners.get(e.type) || [];
        for (let f of listeners) {
            f(e);
        }
        return this;
    }
}

/**
 * An interface for every object that can be extended by plugins.
 *
 * @interface
 */
export default class Extensible {

    /**
     * Adds the given method to this object.
     *
     * @param {String|Symbol} name
     * The name of the method.
     *
     * @param {Function} f
     * The function to execute.
     *
     * @abstract
     */
    [addMethodSym](name, f) {}

    /**
     * Removes the method with the given name from this object.
     *
     * @param {String|Symbol} name
     * The name of the function to remove.
     *
     * @abstract
     */
    [removeMethodSym](name) {}

    /**
     * Adds the given plugins to this object by calling the register method on
     * each plugin and passing this object as an argument.
     *
     * @param {Plugin} plugins
     * The plugins to add.
     *
     * @abstract
     */
    [addPluginSym](plugin) {}

    /**
     * Removes the given plugins from this object by calling the register method
     * on each plugin and passing this object as an argument.
     *
     * @param {Plugin} plugins
     * The plugins to remove.
     *
     * @abstract
     */
    [removePluginSym](plugin) {}
}

/**
 * A symbol for the addMethod method in the Extensible interface.
 *
 * @type {Symbol}
 */
export const addMethodSym = Symbol("addMethod");

/**
 * A symbol for the removeMethod method in the Extensible interface.
 *
 * @type {Symbol}
 */
export const removeMethodSym = Symbol("removeMethod");

/**
 * A symbol for the addPlugin method in the Extensible interface.
 *
 * @type {Symbol}
 */
export const addPluginSym = Symbol("addPlugin");

/**
 * A symbol for the removePlugin method in the Extensible interface.
 *
 * @type {Symbol}
 */
export const removePluginSym = Symbol("removePlugin");

/**
 * A mixin for Extensible behavior.
 *
 * @type {Object}
 */
export const extensibleMixin = {
    addMethod(name, f, override = false) {
        if (!override && this[name]) {
            throw new Error(`A property ${name} exists already on ${this}.`);
        }
        this[name] = f;
        return this;
    },

    removeMethod(name) {
        if (!this[name]) {
            throw new Error(`A property ${name} does not exist on ${this}.`);
        }
        delete this[name];
        return this;
    },

    addPlugin(plugin) {
        plugin.register(this);
        return this;
    },

    removePlugin(plugin) {
        plugin.unregister(this);
        return this;
    }
};

/**
 * A class to generate IDs.
 */
export default class IDGenerator {

    /**
     * @param {String} [prefix=""]
     * The prefix IDs should use.
     */
    constructor(prefix = "") {

        /**
         * The prefix IDs should use.
         *
         * @type {String}
         */
        this.prefix = prefix;

        /**
         * An automatically incremented counter used to generate IDs.
         *
         * @type {Number}
         * @private
         */
        this.counter = 0;
    }

    /**
     * Returns an ID.
     *
     * @return {String}
     * An ID.
     */
    next() {
        return this.prefix + (this.counter++);
    }
}

import EventManager from "./EventManager.js";

/**
 * An interface for every object that allows listeners to be attached to it.
 *
 * @interface
 */
export default class Observable {

    /**
     * Adds an event listener to this object.
     *
     * @param {String|Iterator<String>} types
     * The types of events the function should listen to.
     *
     * @param {Function} f
     * The function that should be executed when the event fires.
     *
     * @param {Object} [context]
     * The object this should refer to inside f.
     *
     * @return {Object}
     * This object to make the method chainable.
     *
     * @abstract
     */
    addListener(types, f, context) {}
}

/**
 * This is a mixin for Observable behavior if an {@link EventManager} is used.
 * Several assumptions are made: The event manager must be reachable by
 * this.eventManager and the EventManager class must be imported under this
 * exact name. This mixin can be used to facade the use of an EventManager.
 *
 * @type {Object}
 */
export const observableMixin = {
    addListener(types, f, context) {
        this.eventManager.addListener(types, f, context);
        return this;
    },

    fireEvent(type, data) {
        const e = EventManager.makeEvent(this, type, data);
        this.eventManager.fireEvent(e);
    },

    bubbleEvent(e, newType) {
        if (newType) {
            e = EventManager.makeEvent(e.source, newType, e.data);
        }
        this.eventManager.fireEvent(e);
    }
};

/**
 * A base class for plugins.
 *
 * @implements {Cloneable}
 */
export default class Plugin {

    /**
     * Adds this plugin to the given object.
     *
     * @param {Object} o
     * The object this plugin should be added to.
     *
     * @abstract
     */
    register(o) {}

    /**
     * Removes this plugin from the given object.
     * 
     * @param {Object} o
     * The object this plugin should be removed from.
     *
     * @abstract
     */
    unregister(o) {}

    /**
     * Returns a copy of this plugin. For indexes you usually do not want to
     * copy its internal data structures but just the configuration so it can be
     * added to a new object. By default this method returns this plugin.
     *
     * @return {Plugin}
     * This object.
     */
    clone() {
        return this;
    }
}

import "core-js"; // TODO remove once no longer needed

export {default as Cloneable, cloneSym, cloneableMixin} from "./Cloneable.js";
export {default as Cloner}                              from "./Cloner.js";
export {default as Color, predefinedColors}             from "./Color.js";
export {default as Event}                               from "./Event.js";
export {default as EventManager}                        from "./EventManager.js";
export {default as Extensible, addMethod, removeMethod,
        addPlugin, removePlugin, extensibleMixin}       from "./Extensible.js";
export {default as IDGenerator}                         from "./IDGenerator.js";
export {default as Observable, observableMixin}         from "./Observable.js";
export {default as Plugin}                              from "./Plugin.js";
